document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".toc"),t=document.getElementById("sticky-toc");if(!e||!t)return;let n=e.offsetTop+e.offsetHeight;function s(){window.scrollY>n?t.classList.add("visible"):t.classList.remove("visible")}function o(){n=e.offsetTop+e.offsetHeight,s()}window.addEventListener("scroll",s),window.addEventListener("resize",o)})